CREATE TABLE "public"."wallet" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "publicKey" text NOT NULL, "privateKey" text NOT NULL, "clientId" uuid NOT NULL, "sol" uuid NOT NULL, "eth" uuid NOT NULL, "secretPhase" text NOT NULL, "bitcoin" uuid NOT NULL, "usdc" uuid NOT NULL, PRIMARY KEY ("id","publicKey") , FOREIGN KEY ("clientId") REFERENCES "public"."client"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"), UNIQUE ("publicKey"), UNIQUE ("privateKey"), UNIQUE ("clientId"), UNIQUE ("sol"), UNIQUE ("eth"), UNIQUE ("secretPhase"), UNIQUE ("bitcoin"), UNIQUE ("usdc"));COMMENT ON TABLE "public"."wallet" IS E'wallets info for clients';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
