CREATE TABLE "public"."bitcoin" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "mainnet" text NOT NULL, "testnet" text NOT NULL, "regtest" text NOT NULL, "regtestBtc" float8 NOT NULL DEFAULT 0.00, "mainnetBtc" float8 NOT NULL DEFAULT 0.00, "textnetBtc" float8 NOT NULL DEFAULT 0.00, "clientId" uuid NOT NULL, "walletId" uuid NOT NULL, PRIMARY KEY ("id","clientId","walletId") , FOREIGN KEY ("clientId") REFERENCES "public"."client"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("walletId") REFERENCES "public"."wallet"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"), UNIQUE ("mainnet"), UNIQUE ("testnet"), UNIQUE ("regtest"), UNIQUE ("clientId"), UNIQUE ("walletId"));COMMENT ON TABLE "public"."bitcoin" IS E'bticoin address for client wallets';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
